package com.arrays.example;

public class SimpleArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] num = new int[5];
		num[0] = 10;
		num[1] = 20;
		num[2] = 30;
		num[3] = 40;
		num[4] = 50;
		
		System.out.println("element at 0 position : " + num[0]);
		System.out.println("element at 1 position : " + num[1]);
		System.out.println("element at 2 position : " + num[2]);
		System.out.println("element at 3 position : " + num[3]);
		System.out.println("element at 4 position : " + num[4]);
	}

}
